Analytics.Script:
CREATE TEMPORARY TABLE esbFlowEvent USING CompressedEventAnalytics OPTIONS(tableName "ESB-FLOW-ENTRY-STREAM", schema "messageFlowId STRING, host STRING, componentType STRING, componentIndex INT,componentId STRING, startTime LONG, endTime LONG, duration INT, beforePayload STRING, afterPayload STRING, contextPropertyMap STRING, transportPropertyMap STRING, children STRING, entryPoint STRING, faultCount INT");
CREATE TEMPORARY TABLE esbEvent USING CarbonAnalytics OPTIONS (tableName "ESB-EVENT", schema "messageFlowId FACET -i, host STRING, componentType FACET -i, componentIndex INT, componentId FACET -i, startTime LONG -i, endTime LONG -i, duration INT, beforePayload STRING, afterPayload STRING, contextPropertyMap STRING, transportPropertyMap STRING, children STRING, entryPoint FACET -i, faultCount INT", primaryKeys "messageFlowId, componentId, componentType, componentIndex", mergeSchema "false");
INSERT INTO TABLE esbEvent SELECT messageFlowId, host, componentType, componentIndex, componentId, startTime, endTime, duration, beforePayload, afterPayload, contextPropertyMap, transportPropertyMap, children, entryPoint, faultCount FROM esbFlowEvent;
CREATE TEMPORARY TABLE esbServiceStatPerSecond USING CarbonAnalytics OPTIONS (tableName "ESB-Stat-per-Second", schema "year INT -i, month INT -i, day INT -i, hour INT -I, minute INT -i, second INT -i, componentId FACET -i, componentType FACET -i, totalDuration LONG, minDuration INT, maxDuration INT, noOfInvocation LONG -sp, faultCount INT, startingTime LONG -i, facetStartTime FACET -i", primaryKeys "year, month, day, hour, minute, second, componentId, componentType", mergeSchema "false");
INSERT INTO TABLE esbServiceStatPerSecond SELECT year, month, day, hour, minute, second, componentId, componentType, totalDuration, minDuration, maxDuration, noOfInvocation, totalFault, getSecondStartingTime(year, month, day, hour, minute, second) as startingTime, cast(getSecondStartingTime(year, month, day, hour, minute, second) as STRING) as facetStartTime FROM (SELECT temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.second, temp.componentId, temp.componentType, sum(duration) as totalDuration, count(*) as noOfInvocation, sum(faultCount) as totalFault, min(duration) as minDuration, max(duration) as maxDuration FROM (SELECT getYear(startTime) as year, getMonth(startTime) as month, getDay(startTime) as day, getHour(startTime) as hour, getMinute(startTime) as minute, getSeconds(startTime) as second, duration as duration, componentType, componentId, (CASE WHEN faultCount > 0 then 1 ELSE 0 END) as faultCount FROM esbFlowEvent) temp WHERE temp.componentType = "Proxy Service" OR temp.componentType = "API" OR temp.componentType = "Endpoint" GROUP BY temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.second, temp.componentId, temp.componentType) result;
