INSERT INTO TABLE esbServiceStatPerMinute SELECT year, month, day, hour, minute, componentId, componentType, totalDuration, minDuration, maxDuration, noOfInvocation, totalFault, getMinuteStartingTime(year, month, day, hour, minute) as startingTime, cast(getMinuteStartingTime(year, month, day, hour, minute) as STRING) as facetStartTime FROM (SELECT temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.componentId, temp.componentType, sum(duration) as totalDuration, sum(faultCount) as totalFault, count(*) as noOfInvocation, min(duration) as minDuration, max(duration) as maxDuration FROM (SELECT getYear(startTime) as year, getMonth(startTime) as month, getDay(startTime) as day, getHour(startTime) as hour, getMinute(startTime) as minute, duration as duration, componentType, componentId, (CASE WHEN faultCount > 0 then 1 ELSE 0 END) as faultCount FROM esbFlowEvent) temp WHERE temp.componentType = "Proxy Service" OR temp.componentType = "API" OR temp.componentType = "Endpoint" GROUP BY temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.componentId, temp.componentType) result;
