CREATE TEMPORARY TABLE esbServiceStatPerMonth USING CarbonAnalytics OPTIONS (tableName "ESB-Stat-per-Month", schema "year INT -i, month INT -i, componentId FACET -i, componentType FACET -i, totalDuration LONG, minDuration INT, maxDuration INT, noOfInvocation LONG -sp, faultCount INT, startingTime LONG -i, facetStartTime FACET -i", primaryKeys "year, month, componentId, componentType", mergeSchema "false");
