CREATE TEMPORARY TABLE mediatorStatPerSecond USING CarbonAnalytics OPTIONS (tableName "mediator-stat-per-second", schema "year INT -i, month INT -i, day INT -i, hour INT -I, minute INT -i, second INT -i, entryPoint FACET -i, componentIndex INT, componentId FACET -i, componentType FACET -i, totalDuration LONG, minDuration INT, maxDuration INT, noOfInvocation LONG -sp, faultCount INT, startingTime LONG -i, facetStartTime FACET -i", primaryKeys "year, month, day, hour, minute, second, entryPoint, componentIndex", mergeSchema "false");
INSERT INTO TABLE mediatorStatPerSecond SELECT temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.second, temp.entryPoint, temp.componentIndex, temp.componentId, temp.componentType, sum(duration) as totalDuration, min(duration) as minDuration, max(duration) as maxDuration, count(*) as noOfInvocation, sum(faultCount) as totalFault, getSecondStartingTime(temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.second) as startingTime, cast(getSecondStartingTime(year, month, day, hour, minute, second) as STRING) as facetStartTime FROM (SELECT getYear(startTime) as year, getMonth(startTime) as month, getDay(startTime) as day, getHour(startTime) as hour, getMinute(startTime) as minute, getSeconds(startTime) as second, duration as duration, entryPoint, componentIndex, componentType, componentId, (CASE WHEN faultCount > 0 then 1 ELSE 0 END) as faultCount FROM esbFlowEvent) temp WHERE temp.componentType = "Mediator" OR temp.componentType = "Sequence" GROUP BY temp.year, temp.month, temp.day, temp.hour, temp.minute, temp.second, temp.entryPoint, temp.componentIndex, temp.componentId, temp.componentType;
